export const typeDefs = gql`
  type Query {
    users: [User!]!
    posts: [Post!]!
  }

  type User {
    id: ID!
    username: String!
    firstName: String
    lastName: String
  }

  type Post {
    id: ID!
    content: String!
    image: String
    author: String
  }

  type Mutation {
    createUser(
      username: String!
      firstName: String!
      lastName: String!
      ): User!
    createPost(
      content: String!
      image: String
      author: String
    ): Post
  }
`;

