import User from './user.model'
import Post from './post.model';

export const resolvers = {
  Query: {
    users: () => User.find(),
    posts: () => Post.find()
  },
  Mutation: {
    createUser: async (_root, {username, firstName, lastName}) => {
      const user = new User({username, firstName, lastName});
      await user.save();
      return user;
    },
    createPost: async (_root, newPost) => {
      console.log(newPost)
      const post = new Post(newPost);
      await post.save();
      return post;

    }

  }
}

